import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity } from 'react-native';
import Background from './background';
import Field from './field';
import Btn from './btn';
import { useAuth } from './app/context/AuthContext';


interface LoginProps {
  navigation: any;
}

const Login: React.FC<LoginProps> = ({ navigation }) => {
  
  const {onLogin,onLogout,onRegister}=useAuth();
  const [username,setusername] = useState('');
  const [password, setPassword] = useState('');

  const login = async()=>{
    const result = await onLogin!(username,password);
    
    if(result && result.error){
      alert('error while login')  
    }
  }

  

 

  return (
    <Background>
      <View style={{ alignItems: "center", width: 400 }}>
        <Text style={{ color: "white", fontSize: 54, fontWeight: "bold", marginVertical: 10 }}>Login</Text>

        <View style={{ backgroundColor: "white", height: 700, width: 425, borderTopLeftRadius: 100, marginTop: 80, alignItems: 'center', marginLeft: 20, paddingTop: 50 }}>
          <Text style={{ fontSize: 30, color: '#f28500', fontWeight: 'bold', marginTop: 30 }}>Welcome Back</Text>
          <Text style={{ color: "grey", fontSize: 14, fontWeight: "bold", marginBottom: 20 }}>Login to your account</Text>
          <Field placeholder="Email / Username" onChangeText={(text) => setusername(text)} />
          <Field placeholder="Password" secureTextEntry={true} onChangeText={(text) => setPassword(text)} />
          <View style={{ alignItems: 'flex-end', width: '78%', paddingRight: 16, marginBottom: 90 }}>
            <Text style={{ color: 'black', fontWeight: 'bold', fontSize: 16 }}>Forgot Password ?</Text>
          </View>
          <Btn textColor={'white'} bgColor={'#f28500'} btnLabel="Login" onpress={login} />

          <View style={{ display: 'flex', flexDirection: "row", justifyContent: "center" }}>
            <Text style={{ fontSize: 16, fontWeight: 'bold' }}>Don't have an account ? </Text>
            <TouchableOpacity onPress={() => navigation.navigate("Signup")}>
              <Text style={{ color: '#f28500', fontWeight: 'bold', fontSize: 16 }}>signup</Text>
            </TouchableOpacity>

          </View>
        </View>

      </View>
    </Background>
  );
};

export default Login;
