import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import Background from './background';
import Field from './field';
import Btn from './btn';
import { useAuth } from './app/context/AuthContext';

const Signup = (props) => {
  const { onRegister,onLogin } = useAuth();
  const [firstname, setFirstname] = useState('');
  const [lastname, setLastname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const handleSignup = () => {
    console.log("handle sign up function")
    onRegister(firstname,lastname,email,password);
  };
  
  return (
    <Background>
      <View style={{ alignItems: "center", width: 400 }}>
        <Text style={{ color: "white", fontSize: 54, fontWeight: "bold", marginTop: 20 }}>Register</Text>
        <Text style={{ color: 'white', fontSize: 16, marginBottom: 20, fontWeight: 'bold' }}>Create a new account</Text>
        <View style={{ backgroundColor: "white", height: 700, width: 425, borderTopLeftRadius: 100, marginTop: 40, alignItems: 'center', marginLeft: 20, paddingTop: 50 }}>

          <Field placeholder="Firstname" onChangeText={(text) => setFirstname(text)} />
          <Field placeholder="Lastname" onChangeText={(text) => setLastname(text)} />
          <Field placeholder="Contact Number" keyboardType={"number"} onChangeText={(text) => setEmail(text)} />
          <Field placeholder="Password" secureTextEntry={true} onChangeText={(text) => setPassword(text)} />
          <Field placeholder="Confirm Password" secureTextEntry={true} />
          <Btn textColor={'white'} bgColor={'#f28500'} btnLabel="Signup" onpress={handleSignup} />

          <View style={{ display: 'flex', flexDirection: "row", justifyContent: "center" }}>
            <Text style={{ fontSize: 16, fontWeight: 'bold' }}>Already have an account ? </Text>
            <TouchableOpacity onPress={() => props.navigation.navigate("Login")}>
              <Text style={{ color: '#f28500', fontWeight: 'bold', fontSize: 16 }}>Login</Text>
            </TouchableOpacity>

          </View>
        </View>

      </View>
    </Background>
  );
}

export default Signup;
