import { createContext, useContext, useEffect, useState } from "react";
import axios from 'axios';
import * as SecureStore from 'expo-secure-store';
import { useNavigation } from "@react-navigation/native";
import jwt from 'jsonwebtoken';
import * as Crypto from 'expo-crypto';
import jwtDecode from 'jwt-decode';



interface AuthProps {
    authState?: { token: string | null; authenticated: boolean | null ;role:string|null};
    onRegister?: (firstname:string,lastname:string,email: string, password: string) => Promise<any>;
    onLogin?: (username: string, password: string) => Promise<any>;
    onLogout?: () => Promise<any>;
}

const API_URL_login = 'https://dummyjson.com/auth/login';
const API_URL_Register = '';
const TOKEN_KEY = 'abcd123';
const ROLE_KEY="abcd12"
const AuthContext = createContext<AuthProps>({});

export const useAuth = () => {
    return useContext(AuthContext);
};

export const AuthProvider = ({ children ,navigation}: any) => {
    const [authState, setAuthState] = useState<{
        token: string | null;
        authenticated: boolean | null;
        role:string | null;
    }>({
        token: null,
        authenticated: null,
        role:null
    });
    
    
    useEffect(() => {
        const loadTokenAndRole = async () => {
            const token = await SecureStore.getItemAsync(TOKEN_KEY);
            const role = await SecureStore.getItemAsync(ROLE_KEY); // Assuming you have a ROLE_KEY constant for the role
    
            if (token && role) { // Check both token and role existence
                axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    
                setAuthState({
                    token: token,
                    authenticated: true,
                    role: role, // Set role in the auth state
                });
            }
        };
        loadTokenAndRole();
    }, []);

    const register = async (firstname: string, lastname: string, email: string, password: string) => {
        try {
            console.log("register func")
            const response = await fetch(API_URL_Register, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ firstname, lastname, email, password }),
                
                
            });
            
    
            if (!response.ok) {
                const errorMessage = await response.json();
                return { error: true, msg: errorMessage.msg };
            }
            else if(response.ok){
                
                console.log(response.json)
            }
            return await response.json();

        } catch (e) {
            return { error: true, msg: 'An error occurred while registering.' };
        }
    };
    

    const login = async (username: string, password: string) => {

        try {
             const response = await fetch(API_URL_login, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                     username:username,
                     password:password,
                     "expiresInMins": 1
                     })
            });
            
    
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            
            const result = await response.json();
            const role=result.role;
            
            
            
            setAuthState({
                token: result.token,
                authenticated: true,
                role:role
            });
    
            // Set token in Axios defaults headers
            axios.defaults.headers.common['Authorization'] = `Bearer ${result.token}`;
    
            // Store token securely
            await SecureStore.setItemAsync(TOKEN_KEY, result.token);
            await SecureStore.setItemAsync(ROLE_KEY,result.role)
    


            function parsejwt(token: string){
                var base64url=token.split('.')[1];
                var base64=decodeURIComponent(atob(base64url).split('').map((c)=>{
                    return '%'+('00'+c.charCodeAt(0).toString(16)).slice(-2);
                }).join(''));
                return JSON.parse(base64);
              }
              

            const decodedData = parsejwt(result.token);
            console.log(decodedData)
            return result; 
        } catch (error) {
            return { error: true, msg: error.message };
        }
    };
    

    const logout = async () => {
        await SecureStore.deleteItemAsync(TOKEN_KEY);

        axios.defaults.headers.common['Authorization'] = '';
        setAuthState({
            token: null,
            authenticated: false,
            role:null
        });
        navigation.navigate("Welcome"); // Assuming 'Welcome' is the name of your welcome screen

        
    };

    const value = {
        onRegister: register,
        onLogin: login,
        onLogout: logout,
        authState,
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
    
};
